insert into comment(uid, type, reply_to, reply_to_uid, content, gmt_create, gmt_modified)
values (4,0,1,null,'老刘真的是知识作者的异类，七小时时长放别的UP至少可以分出三四十集来发赚三四十份钱，但老刘真的就是一集出完，硬到无法形容。可是在这个流量为王的快节奏短视频时代有多少人能看到这个视频呢？看到的人又有多少是点进去之后不仅仅惊叹一声评论里面发个问号就走了呢？明明这种方式吃力不讨好可老刘还在坚持，佩服！希望2049能够真正的被更多人发现，各位老板多多三连转发吧!','2021-12-17 11:05','2021-12-17 11:05');
